input_schema:
{
"columns": [
{
"name": "division",
"label": "구분",
"placeholder": "예: 사내이사, 사외이사",
"type": "text"
},
{
"name": "full_name",
"label": "성명",
"placeholder": "예: 천준영",
"type": "text"
},
{
"name": "position",
"label": "직책",
"placeholder": "예: 위원장, 위원",
"type": "text"
},
{
"name": "appointment_date",
"label": "이사 선임일",
"placeholder": "YYYY-MM-DD",
"type": "date"
},
{
"name": "gender",
"label": "성별",
"type": "select",
"options": ["남", "여"]
}
]
}

{
"fields": [
{
"name": "decision_date",
"label": "의결/검토 일자",
"placeholder": "예: 2023년 4월",
"type": "text"
},
{
"name": "decision_body",
"label": "의결/검토 주체",
"placeholder": "예: 이사회 산하 지속가능경영위원회",
"type": "text"
},
{
"name": "decision_details",
"label": "주요 내용",
"placeholder": "예: 기후변화 시나리오 기반 리스크/기회 검토",
"type": "text_long"
}
]
}

{
"fields": [
{
"name": "member_name",
"label": "성명",
"placeholder": "예: 천준영",
"type": "text"
},
{
"name": "member_title",
"label": "직책",
"placeholder": "예: 사외이사, 지속가능경영위원회 위원장",
"type": "text"
},
{
"name": "competency_details",
"label": "핵심 역량 및 경력 사항",
"placeholder": "예: 국제기구 경험, 특정 산업 전문가로서의 경력, 위원회 참여 이력 등을 구체적으로 서술",
"type": "text_long"
}
]
}

{
"fields": [
{
"name": "member_name_and_title",
"label": "구성원 (이름 및 직책)",
"placeholder": "예: 2024년 지속가능경영위원회 위원장 천준영 이사",
"type": "text"
},
{
"name": "activity_details",
"label": "교육/활동 내용",
"placeholder": "예: 기후변화를 포함한 ESG 이슈 대응 역량 강화를 위해 고려대학교에서 관련 과정을 수강",
"type": "text_long"
}
]
}

{
"columns": [
{
"name": "meeting_date",
"label": "개최일자",
"placeholder": "YYYY-MM-DD",
"type": "date"
},
{
"name": "agenda_details",
"label": "의안내용",
"placeholder": "예: 2023년 지속가능성보고서 발간 결과",
"type": "text_long"
},
{
"name": "related_info",
"label": "관련정보",
"placeholder": "예: 보고서 내 ESG 데이터 및 성과 검토",
"type": "text_long"
}
]
}

{
"columns": [
{
"name": "category",
"label": "구분",
"placeholder": "예: 이사회, 지속가능경영위원회",
"type": "text"
},
{
"name": "decision_date",
"label": "일자",
"placeholder": "YYYY-MM-DD",
"type": "date"
},
{
"name": "approval_status",
"label": "가결 여부",
"type": "select",
"options": ["가결", "부결","보고"]
},
{
"name": "agenda_details",
"label": "주요 의안 내용",
"placeholder": "예: 2024년도 ESG 경영전략 및 목표 승인",
"type": "text_long"
},
{
"name": "climate_considerations",
"label": "기후 관련 위험 및 기회 고려사항",
"placeholder": "예: 저탄소 기술 투자 관련 리스크 및 기회 요인 검토",
"type": "text_long"
}
]
}

{
"columns": [
{
"name": "activity_category",
"label": "구분",
"type": "select",
"options": ["진척도 관리감독", "목표설정"]
},
{
"name": "activity_date",
"label": "일자",
"placeholder": "YYYY-MM-DD",
"type": "date"
},
{
"name": "activity_details",
"label": "내용",
"placeholder": "예: 2024년도 온실가스 감축 목표 설정 검토",
"type": "text_long"
}
]
}

{
"fields": [
{
"name": "management_position",
"label": "경영진 직책",
"placeholder": "예: CEO, CSO 겸 CFO",
"type": "text"
},
{
"name": "responsibilities",
"label": "주요 역할 및 책임",
"placeholder": "예: 기후변화 전략 이행 총괄 및 관리감독 수행",
"type": "text_long"
}
]
}

{
"columns": [
{
"name": "category",
"label": "구분",
"type": "select",
"options": ["전환 리스크", "물리적 리스크", "기회"]
},
{
"name": "type",
"label": "유형",
"placeholder": "예: 정책, 기술 등",
"type": "text"
},
{
"name": "factor",
"label": "위험 및 기회 요인",
"placeholder": "예: 탄소배출규제 강화",
"type": "text_long"
},
{
"name": "description",
"label": "설명",
"placeholder": "위험/기회 요인에 대한 상세 설명",
"type": "text_long"
},
{
"name": "short_term_impact",
"label": "단기",
"type": "select",
"options": ["상", "중", "하", "해당없음"]
},
{
"name": "mid_term_impact",
"label": "중기",
"type": "select",
"options": ["상", "중", "하", "해당없음"]
},
{
"name": "long_term_impact",
"label": "장기",
"type": "select",
"options": ["상", "중", "하", "해당없음"]
}
]
}

{
"fields": [
{
"name": "segment_name",
"label": "사업 부문명",
"placeholder": "예: 담배부문, 건강기능부문",
"type": "text"
},
{
"name": "segment_description",
"label": "주요 사업 내용",
"placeholder": "예: 궐련 및 차세대 전자담배 등의 제조와 판매 사업을 운영",
"type": "text_long"
}
]
}

{
"fields": [
{
"name": "risk_opportunity_factor",
"label": "위험 및 기회 요인",
"placeholder": "'위험 및 기회' 카테고리에서 입력한 요인 중 하나를 선택 또는 직접 입력",
"type": "text"
},
{
"name": "overview_description",
"label": "영향 개요 설명",
"placeholder": "해당 위험/기회에 대한 전반적인 설명과 사업에 미치는 주된 영향을 서술해주세요.",
"type": "text_long"
},
{
"name": "impact_details_table",
"label": "활동별 상세 영향",
"type": "table_input",
"guidance": "표의 '활동' 컬럼에는 바로 위 항목에서 답변하신 사업모형 및 가치사슬 활동들을 기재해주세요.",
"schema": {
"columns": [
{
"name": "activity",
"label": "활동",
"type": "text"
},
{
"name": "current_impact",
"label": "현재",
"type": "text_long"
},
{
"name": "future_impact",
"label": "예상",
"type": "text_long"
}
]
}
}
]
}

{
"fields": [
{ "name": "project_name", "label": "프로젝트명", "type": "text", "placeholder": "예: 친환경 하수처리공장 신설" },
{ "name": "project_location_scale", "label": "위치 및 규모", "type": "text", "placeholder": "예: 고려대학교 경영대학 1,583㎡ 부지" },
{ "name": "project_investment", "label": "투자 규모", "type": "text", "placeholder": "예: 약 1,000억 원" },
{ "name": "project_timeline", "label": "주요 일정", "type": "text", "placeholder": "예: 2026년 준공 예정" },
{ "name": "project_details", "label": "프로젝트 상세 내용 및 기대효과", "type": "text_long", "placeholder": "예: 최첨단 하수처리 자동화 도입, 친환경건축인증(LEED) GOLD 등급 목표 등" }
]
}

{
"fields": [
{
"name": "effort_category",
"label": "구분",
"placeholder": "예: ① 제품 소재 변경, ② 재생에너지 조달 및 사용 확대",
"type": "text"
},
{
"name": "detailed_actions",
"label": "세부 활동 내용",
"type": "table_input",
"schema": {
"columns": [
{
"name": "action_description",
"label": "내용",
"type": "text_long"
},
{
"name": "is_mitigation",
"label": "완화",
"type": "boolean"
},
{
"name": "is_adaptation",
"label": "적응",
"type": "boolean"
},
{
"name": "is_current",
"label": "현재",
"type": "boolean"
},
{
"name": "is_future",
"label": "예상",
"type": "boolean"
}
]
}
}
]
}

{
"source_requirement": "str-19",
"source_field_to_display": "action_description",
"fields": [
{
"name": "narrative_text",
"label": "상세 서술 내용",
"type": "text_long",
"placeholder": "해당 활동의 배경, 구체적인 실행 내용, 정량적 성과, 향후 계획 등을 자유롭게 서술해주세요."
}
]
}

{
"fields": [
{
"name": "effort_category",
"label": "구분",
"placeholder": "예: ① 공급망 관리, ② 지역사회 협력",
"type": "text"
},
{
"name": "detailed_actions",
"label": "세부 활동 내용",
"type": "table_input",
"schema": {
"columns": [
{
"name": "action_description",
"label": "내용",
"type": "text_long"
},
{
"name": "is_mitigation",
"label": "완화",
"type": "boolean"
},
{
"name": "is_adaptation",
"label": "적응",
"type": "boolean"
},
{
"name": "is_current",
"label": "현재",
"type": "boolean"
},
{
"name": "is_future",
"label": "예상",
"type": "boolean"
}
]
}
}
]
}

{
"source_requirement": "str-21",
"source_field_to_display": "action_description",
"fields": [
{
"name": "narrative_text",
"label": "상세 서술 내용",
"type": "text_long",
"placeholder": "해당 활동의 배경, 구체적인 실행 내용, 정량적 성과, 향후 계획 등을 자유롭게 서술해주세요."
}
]
}

{
"columns": [
{
"name": "target_category",
"label": "목표 구분",
"placeholder": "예: 온실가스 감축, 재생에너지 사용, 탄소중립(Net-Zero)",
"type": "text"
},
{
"name": "scope",
"label": "대상 범위",
"placeholder": "예: Scope 1+2, 가치사슬 전체",
"type": "text"
},
{
"name": "target_year",
"label": "목표 연도",
"placeholder": "예: 2030, 2045",
"type": "text"
},
{
"name": "target_details",
"label": "상세 목표 내용",
"placeholder": "예: 2020년 대비 42% 감축, 100% 전환",
"type": "text_long"
}
]
}

{
"columns": [
{
"name": "assumption",
"label": "주요 가정",
"placeholder": "예: 예상 온실가스 배출량 산정 조건",
"type": "text_long"
},
{
"name": "dependency",
"label": "의존 요소",
"placeholder": "예: 전환 계획 이행을 위한 물리적, 인적 자원의 가용성",
"type": "text_long"
}
]
}

{
"columns": [
{
"name": "action_category",
"label": "구분",
"placeholder": "예: 자체 태양광 발전 설비 확대, 고효율 설비 전환",
"type": "text"
},
{
"name": "action_plan",
"label": "달성 계획",
"placeholder": "예: 2023년부터 주요 사업장에 태양광 설비를 가동하여 2030년까지 감축 포트폴리오의 21% 달성",
"type": "text_long"
}
]
}

{
"fields": [
{ "name": "financing_type", "label": "자금 조달 유형", "type": "text", "placeholder": "예: 그린본드(Green Bond)" },
{ "name": "issue_date", "label": "발행/조달 일자", "type": "text", "placeholder": "예: 2024년 4월" },
{ "name": "scale", "label": "규모", "type": "text", "placeholder": "예: 1,000억 원" },
{ "name": "use_of_proceeds", "label": "자금 활용 계획", "type": "text_long", "placeholder": "예: 신재생에너지, 친환경 건축물 구축 등 프로젝트 투자" }
]
}

{
"fields": [
{ "name": "hiring_period", "label": "채용 시기", "type": "text", "placeholder": "예: 2023년 이후" },
{ "name": "num_of_hires", "label": "채용 인원", "type": "number", "placeholder": "예: 4" },
{ "name": "roles_and_plans", "label": "주요 역할 및 운영 계획", "type": "text_long", "placeholder": "예: 관련 부서에 배치하여 온실가스 저감 계획 수립, 모니터링 등 관련 업무 수행" }
]
}

{
"fields": [
{
"name": "step_description",
"label": "분석 단계",
"placeholder": "예: 1단계 - 주요 리스크/기회 식별",
"type": "text"
}
]
}

{
"columns": [
{
"name": "scenario_name",
"label": "시나리오",
"placeholder": "예: 1.5℃ 시나리오 (NZE)",
"type": "text"
},
{
"name": "key_assumptions",
"label": "주요 가정",
"placeholder": "예: 강력한 탄소 규제 및 저탄소 경제로의 급격한 전환",
"type": "text_long"
},
{
"name": "input_variables",
"label": "핵심 변수 (투입변수)",
"placeholder": "예: 탄소 가격(Carbon Price), 신재생에너지 비중, 폭염 발생 빈도 등",
"type": "text_long"
}
]
}

{
"source_requirement": "str-4",
"source_field_to_display": "factor",
"fields": [
{
"name": "financial_impact",
"label": "재무적 영향",
"type": "text_long",
"placeholder": "해당 요인이 기업의 재무 상태에 미치는 구체적인 영향(비용 증가, 매출 변동 등)을 서술해주세요."
},
{
"name": "response_strategy",
"label": "대응 현황",
"type": "text_long",
"placeholder": "해당 요인에 대응하기 위한 기업의 현재 및 향후 계획, 전략 등을 서술해주세요."
}
]
}

{
"source_requirement": "str-4",
"source_filter": { "category": ["전환 리스크"] },
"source_field_to_display": "factor",
"fields": [
{
"name": "analysis_overview",
"label": "분석 개요 및 시나리오 분석 결과",
"type": "text_long",
"placeholder": "해당 전환 리스크를 중요하게 선정한 이유와, 시나리오 분석을 통해 나타난 리스크 수준의 변화(시기별, 시나리오별)를 설명해주세요."
},
{
"name": "context_and_impact",
"label": "시장/규제 동향 및 가치사슬 영향",
"type": "text_long",
"placeholder": "해당 리스크와 관련된 외부 동향(규제, 시장 가격 등)과, 이 리스크가 기업의 가치사슬(운영, 물류 등)에 미치는 영향을 설명해주세요."
},
{
"name": "quantification_methodology",
"label": "재무적 영향 산출 방법론",
"type": "text_long",
"placeholder": "재무적 영향을 추정하기 위해 사용한 기준, 가정, 계산 방법 등을 설명해주세요."
},
{
"name": "financial_statement_impact",
"label": "재무제표 영향",
"type": "text_long",
"placeholder": "해당 전환 리스크가 손익계산서, 재무상태표, 현금흐름표의 특정 계정(매출원가, 충당부채 등)에 미치는 영향을 구체적으로 서술해주세요."
}
]
}

{
"columns": [
{
"name": "risk_type",
"label": "리스크 유형",
"type": "select",
"options": ["폭염", "태풍", "가뭄", "홍수", "수자원 스트레스"]
},
{
"name": "location",
"label": "사업장/지역",
"placeholder": "예: 청주, 용인, 중국 우시",
"type": "text"
},
{
"name": "scenario",
"label": "시나리오",
"placeholder": "예: SSP1-2.6, SSP5-8.5",
"type": "text"
},
{
"name": "year",
"label": "연도",
"placeholder": "예: 2030, 2040, 2050",
"type": "number"
},
{
"name": "value",
"label": "측정값 (증가량)",
"placeholder": "예: 14.7",
"type": "number"
},
{
"name": "unit",
"label": "단위",
"placeholder": "예: 일, mm, 건",
"type": "text"
}
]
}

{
"columns": [
{
"name": "consideration_item",
"label": "재무 영향 산정을 위해 고려한 항목",
